    // The values being non-zero value makes deployment a bit more expensive,
    // but in exchange the refund on every call to nonReentrant will be lower in
    // amount. Since refunds are capped to a percentage of the total
    // transaction's gas, it is best to keep them low in cases like this one, to
    // increase the likelihood of the full refund coming into effect.
    uint256 private constant NOT_ENTERED = 1;
    uint256 private constant ENTERED = 2;

    uint256 private _status;

    /**
     * @dev Unauthorized reentrant call.
     */
    error ReentrancyGuardReentrantCall();

    constructor() {
        _status = NOT_ENTERED;
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and making it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/BlueCarbonCredit.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";

/**
 * @title BlueCarbonCredit
 * @dev ERC20 token representing blue carbon credits
 * 1 token = 1 tonne CO2 equivalent
 * Decimals = 0 (whole tonnes only)
 */
contract BlueCarbonCredit is ERC20, AccessControl, ERC20Pausable, ERC20Burnable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // Struct to store credit batch metadata
    struct CreditBatch {
        uint256 projectId;
        uint256 verificationId;
        string metadataIPFSHash; // IPFS hash for detailed metadata
        address developer;
        uint256 verifiedAmount;
        uint256 issuanceDate;
        bool isRetired;
        string retirementReason;
        string retirementCertificateIPFS;
    }

    // Mapping from batch ID to credit batch info
    mapping(uint256 => CreditBatch) public creditBatches;
    
    // Mapping from token ID to batch ID
    mapping(uint256 => uint256) public tokenToBatch;
    
    // Mapping to track retired tokens
    mapping(uint256 => bool) public retiredTokens;
    
    // Current batch ID counter
    uint256 public currentBatchId;
    
    // Current token ID counter
    uint256 public currentTokenId;

    // Events
    event CreditBatchMinted(
        uint256 indexed batchId,
        uint256 indexed projectId,
        uint256 indexed verificationId,
        address developer,
        uint256 amount,
        string metadataIPFSHash
    );
    
    event CreditRetired(
        uint256 indexed tokenId,
        uint256 indexed batchId,
        address indexed retiredBy,
        string reason,
        string certificateIPFS
    );

    event BatchRetired(
        uint256 indexed batchId,
        address indexed retiredBy,
        string reason,
        string certificateIPFS
    );

    constructor(address defaultAdmin) ERC20("Blue Carbon Credit", "BCC") {
        _grantRole(DEFAULT_ADMIN_ROLE, defaultAdmin);
        _grantRole(MINTER_ROLE, defaultAdmin);
        _grantRole(VERIFIER_ROLE, defaultAdmin);
        _grantRole(PAUSER_ROLE, defaultAdmin);
    }

    /**
     * @dev Returns 0 decimals as credits represent whole tonnes
     */
    function decimals() public pure override returns (uint8) {
        return 0;
    }

    /**
     * @dev Mint a new batch of carbon credits
     * Only callable by verified minters (admin/verifiers)
     */
    function mintCreditBatch(
        uint256 projectId,
        uint256 verificationId,
        address developer,
        uint256 amount,
        string memory metadataIPFSHash
    ) external onlyRole(MINTER_ROLE) whenNotPaused returns (uint256 batchId) {
        require(developer != address(0), "Invalid developer address");
        require(amount > 0, "Amount must be greater than 0");
        require(bytes(metadataIPFSHash).length > 0, "Metadata IPFS hash required");

        batchId = currentBatchId++;
        
        // Create credit batch
        creditBatches[batchId] = CreditBatch({
            projectId: projectId,
            verificationId: verificationId,
            metadataIPFSHash: metadataIPFSHash,
            developer: developer,
            verifiedAmount: amount,
            issuanceDate: block.timestamp,
            isRetired: false,
            retirementReason: "",
            retirementCertificateIPFS: ""
        });

        // Mint tokens to developer
        _mint(developer, amount);

        // Map tokens to batch (simplified - in practice, you might want individual token tracking)
        for (uint256 i = 0; i < amount; i++) {
            tokenToBatch[currentTokenId + i] = batchId;
        }
        currentTokenId += amount;

        emit CreditBatchMinted(
            batchId,
            projectId,
            verificationId,
            developer,
            amount,
            metadataIPFSHash
        );
    }

    /**
     * @dev Retire carbon credits by burning them
     * Credits are permanently removed from circulation
     */
    function retireCredits(
        uint256 amount,
        string memory reason,
        string memory certificateIPFS
    ) external {
        require(amount > 0, "Amount must be greater than 0");
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");
        require(bytes(reason).length > 0, "Retirement reason required");

        // Burn the tokens
        _burn(msg.sender, amount);

        // For simplicity, we'll emit a retirement event
        // In a more complex implementation, you'd track individual token retirements
        emit CreditRetired(
            0, // tokenId - simplified
            0, // batchId - would need more complex logic to determine
            msg.sender,
            reason,
            certificateIPFS
        );
    }

    /**
     * @dev Retire an entire credit batch
     * Only callable by the batch owner or admin
     */
    function retireCreditBatch(
        uint256 batchId,
        string memory reason,
        string memory certificateIPFS
    ) external {
        require(batchId < currentBatchId, "Invalid batch ID");
        CreditBatch storage batch = creditBatches[batchId];
        require(!batch.isRetired, "Batch already retired");
        require(
            msg.sender == batch.developer || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "Not authorized to retire this batch"
        );
        require(bytes(reason).length > 0, "Retirement reason required");

        // Mark batch as retired
        batch.isRetired = true;
        batch.retirementReason = reason;
        batch.retirementCertificateIPFS = certificateIPFS;

        emit BatchRetired(batchId, msg.sender, reason, certificateIPFS);
    }

    /**
     * @dev Get credit batch information
     */
    function getCreditBatch(uint256 batchId) external view returns (CreditBatch memory) {
        require(batchId < currentBatchId, "Invalid batch ID");
        return creditBatches[batchId];
    }

    /**
     * @dev Check if a token is retired
     */
    function isTokenRetired(uint256 tokenId) external view returns (bool) {
        uint256 batchId = tokenToBatch[tokenId];
        return creditBatches[batchId].isRetired || retiredTokens[tokenId];
    }

    /**
     * @dev Get metadata IPFS hash for a token
     */
    function getTokenMetadata(uint256 tokenId) external view returns (string memory) {
        uint256 batchId = tokenToBatch[tokenId];
        return creditBatches[batchId].metadataIPFSHash;
    }

    /**
     * @dev Pause contract (admin only)
     */
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause contract (admin only)
     */
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @dev Override update function for OpenZeppelin v5 compatibility
     */
    function _update(
        address from,
        address to,
        uint256 value
    ) internal virtual override(ERC20, ERC20Pausable) {
        super._update(from, to, value);
        
        // Allow minting and burning
        if (from == address(0) || to == address(0)) {
            return;
        }
        
        // For simplicity, we're not implementing individual token retirement checks
        // In a full implementation, you'd need to track individual token retirements
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/CarbonCreditMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title CarbonCreditMarketplace
 * @dev Marketplace for trading blue carbon credits
 * Supports both fixed-price listings and auction mechanisms
 */
contract CarbonCreditMarketplace is ReentrancyGuard, Ownable, Pausable {
    using SafeERC20 for IERC20;

    // The carbon credit token contract
    IERC20 public immutable carbonCreditToken;
    
    // Marketplace fee (in basis points, e.g., 250 = 2.5%)
    uint256 public marketplaceFee;
    
    // Fee recipient address
    address public feeRecipient;

    // Listing structure
    struct Listing {
        uint256 listingId;
        address seller;
        uint256 amount;
        uint256 pricePerCredit; // Price in wei per credit
        uint256 totalPrice; // Total price for all credits
        bool isActive;
        uint256 createdAt;
        uint256 expiresAt;
        string metadataIPFS; // Additional listing metadata
    }

    // Offer structure for buyers
    struct Offer {
        uint256 offerId;
        uint256 listingId;
        address buyer;
        uint256 amount;
        uint256 pricePerCredit;
        uint256 totalPrice;
        uint256 expiresAt;
        bool isActive;
    }

    // Mapping from listing ID to listing
    mapping(uint256 => Listing) public listings;
    
    // Mapping from offer ID to offer
    mapping(uint256 => Offer) public offers;
    
    // Mapping from seller to their listing IDs
    mapping(address => uint256[]) public sellerListings;
    
    // Mapping from buyer to their offer IDs
    mapping(address => uint256[]) public buyerOffers;
    
    // Counter for listing IDs
    uint256 public nextListingId;
    
    // Counter for offer IDs
    uint256 public nextOfferId;

    // Events
    event ListingCreated(
        uint256 indexed listingId,
        address indexed seller,
        uint256 amount,
        uint256 pricePerCredit,
        uint256 totalPrice,
        uint256 expiresAt
    );

    event ListingCancelled(uint256 indexed listingId, address indexed seller);

    event ListingPurchased(
        uint256 indexed listingId,
        address indexed buyer,
        address indexed seller,
        uint256 amount,
        uint256 totalPrice
    );

    event OfferCreated(
        uint256 indexed offerId,
        uint256 indexed listingId,
        address indexed buyer,
        uint256 amount,
        uint256 pricePerCredit,
        uint256 totalPrice
    );

    event OfferAccepted(
        uint256 indexed offerId,
        uint256 indexed listingId,
        address indexed seller,
        address buyer,
        uint256 amount,
        uint256 totalPrice
    );

    event OfferCancelled(uint256 indexed offerId, address indexed buyer);

    constructor(
        address _carbonCreditToken,
        uint256 _marketplaceFee,
        address _feeRecipient
    ) Ownable(msg.sender) {
        require(_carbonCreditToken != address(0), "Invalid token address");
        require(_feeRecipient != address(0), "Invalid fee recipient");
        require(_marketplaceFee <= 1000, "Fee too high"); // Max 10%

        carbonCreditToken = IERC20(_carbonCreditToken);
        marketplaceFee = _marketplaceFee;
        feeRecipient = _feeRecipient;
    }

    /**
     * @dev Create a new listing to sell carbon credits
     */
    function createListing(
        uint256 amount,
        uint256 pricePerCredit,
        uint256 duration, // Duration in seconds
        string memory metadataIPFS
    ) external nonReentrant whenNotPaused {
        require(amount > 0, "Amount must be greater than 0");
        require(pricePerCredit > 0, "Price must be greater than 0");
        require(duration > 0, "Duration must be greater than 0");
        require(
            carbonCreditToken.balanceOf(msg.sender) >= amount,
            "Insufficient carbon credit balance"
        );

        // Transfer credits to marketplace for escrow
        carbonCreditToken.safeTransferFrom(msg.sender, address(this), amount);

        uint256 listingId = nextListingId++;
        uint256 totalPrice = amount * pricePerCredit;
        uint256 expiresAt = block.timestamp + duration;

        listings[listingId] = Listing({
            listingId: listingId,
            seller: msg.sender,
            amount: amount,
            pricePerCredit: pricePerCredit,
            totalPrice: totalPrice,
            isActive: true,
            createdAt: block.timestamp,
            expiresAt: expiresAt,
            metadataIPFS: metadataIPFS
        });

        sellerListings[msg.sender].push(listingId);

        emit ListingCreated(
            listingId,
            msg.sender,
            amount,
            pricePerCredit,
            totalPrice,
            expiresAt
        );
    }

    /**
     * @dev Purchase credits directly from a listing
     */
    function purchaseListing(uint256 listingId, uint256 amount)
        external
        payable
        nonReentrant
        whenNotPaused
    {
        Listing storage listing = listings[listingId];
        require(listing.isActive, "Listing not active");
        require(block.timestamp < listing.expiresAt, "Listing expired");
        require(amount > 0 && amount <= listing.amount, "Invalid amount");
        require(msg.sender != listing.seller, "Cannot buy own listing");

        uint256 totalCost = amount * listing.pricePerCredit;
        require(msg.value >= totalCost, "Insufficient payment");

        // Calculate fees
        uint256 fee = (totalCost * marketplaceFee) / 10000;
        uint256 sellerPayment = totalCost - fee;

        // Update listing
        listing.amount -= amount;
        if (listing.amount == 0) {
            listing.isActive = false;
        }

        // Transfer carbon credits to buyer
        carbonCreditToken.safeTransfer(msg.sender, amount);

        // Transfer payments
        payable(listing.seller).transfer(sellerPayment);
        if (fee > 0) {
            payable(feeRecipient).transfer(fee);
        }

        // Refund excess payment
        if (msg.value > totalCost) {
            payable(msg.sender).transfer(msg.value - totalCost);
        }

        emit ListingPurchased(
            listingId,
            msg.sender,
            listing.seller,
            amount,
            totalCost
        );
    }

    /**
     * @dev Cancel a listing and return credits to seller
     */
    function cancelListing(uint256 listingId) external nonReentrant {
        Listing storage listing = listings[listingId];
        require(listing.seller == msg.sender, "Not the seller");
        require(listing.isActive, "Listing not active");

        listing.isActive = false;

        // Return credits to seller
        carbonCreditToken.safeTransfer(msg.sender, listing.amount);

        emit ListingCancelled(listingId, msg.sender);
    }

    /**
     * @dev Create an offer for a specific listing
     */
    function createOffer(
        uint256 listingId,
        uint256 amount,
        uint256 pricePerCredit,
        uint256 duration
    ) external payable nonReentrant whenNotPaused {
        Listing storage listing = listings[listingId];
        require(listing.isActive, "Listing not active");
        require(block.timestamp < listing.expiresAt, "Listing expired");
        require(amount > 0 && amount <= listing.amount, "Invalid amount");
        require(pricePerCredit > 0, "Price must be greater than 0");
        require(duration > 0, "Duration must be greater than 0");
        require(msg.sender != listing.seller, "Cannot offer on own listing");

        uint256 totalPrice = amount * pricePerCredit;
        require(msg.value >= totalPrice, "Insufficient payment for offer");

        uint256 offerId = nextOfferId++;
        uint256 expiresAt = block.timestamp + duration;

        offers[offerId] = Offer({
            offerId: offerId,
            listingId: listingId,
            buyer: msg.sender,
            amount: amount,
            pricePerCredit: pricePerCredit,
            totalPrice: totalPrice,
            expiresAt: expiresAt,
            isActive: true
        });

        buyerOffers[msg.sender].push(offerId);

        // Refund excess payment
        if (msg.value > totalPrice) {
            payable(msg.sender).transfer(msg.value - totalPrice);
        }

        emit OfferCreated(
            offerId,
            listingId,
            msg.sender,
            amount,
            pricePerCredit,
            totalPrice
        );
    }

    /**
     * @dev Accept an offer (seller only)
     */
    function acceptOffer(uint256 offerId) external nonReentrant whenNotPaused {
        Offer storage offer = offers[offerId];
        require(offer.isActive, "Offer not active");
        require(block.timestamp < offer.expiresAt, "Offer expired");

        Listing storage listing = listings[offer.listingId];
        require(listing.seller == msg.sender, "Not the seller");
        require(listing.isActive, "Listing not active");
        require(listing.amount >= offer.amount, "Insufficient credits in listing");

        // Calculate fees
        uint256 fee = (offer.totalPrice * marketplaceFee) / 10000;
        uint256 sellerPayment = offer.totalPrice - fee;

        // Update listing and offer status
        listing.amount -= offer.amount;
        if (listing.amount == 0) {
            listing.isActive = false;
        }
        offer.isActive = false;

        // Transfer carbon credits to buyer
        carbonCreditToken.safeTransfer(offer.buyer, offer.amount);

        // Transfer payments
        payable(msg.sender).transfer(sellerPayment);
        if (fee > 0) {
            payable(feeRecipient).transfer(fee);
        }

        emit OfferAccepted(
            offerId,
            offer.listingId,
            msg.sender,
            offer.buyer,
            offer.amount,
            offer.totalPrice
        );
    }

    /**
     * @dev Cancel an offer and refund buyer
     */
    function cancelOffer(uint256 offerId) external nonReentrant {
        Offer storage offer = offers[offerId];
        require(offer.buyer == msg.sender, "Not the buyer");
        require(offer.isActive, "Offer not active");

        offer.isActive = false;

        // Refund buyer
        payable(msg.sender).transfer(offer.totalPrice);

        emit OfferCancelled(offerId, msg.sender);
    }

    /**
     * @dev Get active listings (paginated)
     */
    function getActiveListings(uint256 offset, uint256 limit)
        external
        view
        returns (Listing[] memory)
    {
        uint256 totalListings = nextListingId;
        if (offset >= totalListings) {
            return new Listing[](0);
        }

        uint256 end = offset + limit;
        if (end > totalListings) {
            end = totalListings;
        }

        uint256 activeCount = 0;
        for (uint256 i = offset; i < end; i++) {
            if (listings[i].isActive && block.timestamp < listings[i].expiresAt) {
                activeCount++;
            }
        }

        Listing[] memory activeListings = new Listing[](activeCount);
        uint256 currentIndex = 0;

        for (uint256 i = offset; i < end; i++) {
            if (listings[i].isActive && block.timestamp < listings[i].expiresAt) {
                activeListings[currentIndex] = listings[i];
                currentIndex++;
            }
        }

        return activeListings;
    }

    /**
     * @dev Update marketplace fee (owner only)
     */
    function setMarketplaceFee(uint256 _fee) external onlyOwner {
        require(_fee <= 1000, "Fee too high"); // Max 10%
        marketplaceFee = _fee;
    }

    /**
     * @dev Update fee recipient (owner only)
     */
    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        require(_feeRecipient != address(0), "Invalid fee recipient");
        feeRecipient = _feeRecipient;
    }

    /**
     * @dev Pause contract (owner only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (owner only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency withdraw (owner only)
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            payable(owner()).transfer(balance);
        }
    }
}

